<style lang="scss">

#fullscreen__slider {
  height: 100vh;
  width: 100%;
  position: relative;
  .slider {
    position: relative;
    width: 100%;
    height: 100%;
    overflow: hidden;
    .slider--cell {
      width: 100%;
      height: 100vh;
      background-repeat: no-repeat;
      background-position: center;
      background-size: cover;
      .slide--caption {
        position: absolute;
        background: white;
        left: 4%;
        bottom: 100px;
        z-index: 1;
      }
    }
  }
}

//SLIDER BUTTONS
.slider--button {
  position: absolute;
  background: transparent;
  border: 0px;
  outline: 0;
  top: 0;
  bottom: 0;
  width: 10%;
  &.button--left {
    left: 0;
    &:hover {
        cursor: url('data:image/png;base64,'), auto;
    }
  }
  &.button--right {
    right: 0;
    &:hover {
        cursor: url('data:image/png;base64,'), auto;
    }
  }
}



</style>

<template>
  <div id="fullscreen__slider">
    <no-ssr>
      <flickity v-if="slides" ref="flickity" :options="flickityOptions" class="slider">
          <div class="slider--cell" v-for="slide in slides" :style="{ backgroundImage: 'url(' + slide.acf.slide_image.url + ')' }">
            <div class="slide--caption" v-html="slide.acf.slide_caption"></div>
          </div>
      </flickity>
    </no-ssr>

    <button @click="previous()" class="slider--button button--left"></button>
    <button @click="next()" class="slider--button button--right"></button>

  </div>
</template>

<script>
import { mapState } from 'vuex';

export default {
  data() {
    return {
      flickityOptions: {
        wrapAround: true,
        autoPlay: true,
        resize: true,
        setGallerySize: false,
        pageDots: false,
        prevNextButtons: false
        // any options from Flickity can be used
      }
    };
  },

  methods: {
    next() {
      this.$refs.flickity.next();
    },

    previous() {
      this.$refs.flickity.previous();
    }
  },
  computed: mapState(['slides'])
};
</script>
